var promoMinutesLeft=10;var promoSecondsLeft=0;var promoUpdateIntervalMs=1000;function updatePromoTimer(){if(0==promoSecondsLeft&&0==promoMinutesLeft){window.location.reload()}else{if(0==promoSecondsLeft){promoSecondsLeft=59;promoMinutesLeft--}else{promoSecondsLeft--}document.getElementById("promoTimeLeftMin").innerHTML=promoMinutesLeft;document.getElementById("promoTimeLeftSec").innerHTML=promoSecondsLeft}}function startPromoTimer(a,b){promoMinutesLeft=a;promoSecondsLeft=b;setInterval("updatePromoTimer();",promoUpdateIntervalMs)};